import React , {useRef, useState} from 'react'
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';




const NoteList = (props) =>{
    // create edit state to show input and not show input 
    const [edit, setEdit] = useState(false)

    // create empty state to store the edit input value
    const [input, setInput] = useState('');

    // useref to get the dom ele list and add class to it for styling
    let inputref = useRef()

    const deletebtn = () =>{
        inputref.current.className = "active"
        setTimeout(() => {
            props.delete(props.id)
        }, 300);
        
    }

    const edithandle = () =>{
        setEdit(true)
      
    }

    const savebtn = ( ) =>{
        // in save function we passing id of button and edit input value
        props.save(props.id, input)
        setEdit(false)
      
    }
    const canceledit = () =>{
        setEdit(false)
    }

// if edit state is true then show the input
    if(edit){
        return <li  className = "edit-list">
            <Input defaultValue = {props.note} onInput={e => setInput(e.target.value)} className = "edit-input"  style = {{color: 'white'}}/>
            <Button onClick = {savebtn} color="primary" >Save</Button>
            <Button onClick = {canceledit} color="primary" >Cancel</Button>
        </li>
    }


    return(
        <>
        <ul  className = "list">
        {props.title && props.note ? <li ref={inputref} >
        <IconButton onClick = {deletebtn} className = "delete-btn" color="primary">
        <DeleteIcon/></IconButton>
        <IconButton onClick = {edithandle} className = "edit-btn" color="primary">
        <EditIcon /></IconButton>
        <span>{props.title}</span><li>{props.note}</li></li> : null }  
        </ul>     
         
        </>
    )
}


export default NoteList;
