import React , {useRef, useState} from 'react'
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';




const NoteList = (props) =>{
    const [edit, setEdit] = useState(false)
    let inputref = useRef()
    const deletebtn = () =>{
        inputref.current.className = "active"
        setTimeout(() => {
            props.delete(props.id)
        }, 300);
        
    }

    const editbtn = () =>{
        setEdit(true)
    }

    if(edit){
        return "Editing"
    }


    return(
        <>
         <ul  className = "list">
       {props.title && props.note ? <li ref={inputref} >
       <IconButton onClick = {deletebtn} className = "delete-btn" color="primary">
        <DeleteIcon/></IconButton>
        <IconButton onClick = {editbtn} className = "edit-btn" color="primary">
        <EditIcon/></IconButton>
        <span>{props.title}</span><li>{props.note}</li></li> : null }  
        </ul>     
         
        </>
    )
}


export default NoteList;
