import React, { useState }  from 'react';


const App = ()=>{
    let color = 'yellow'
    const [bg, update] = useState(color)
    const [text, updateT] = useState('Click me')

    
const Clicked = () =>{
   let newColor = 'red'
   update(newColor)
   updateT('Wow')
 }

 
  return(
      <>
         
         <div style = {{backgroundColor : bg}}>
         <button onClick = {Clicked}>{text}</button>
         </div>
         </>
    )
}


export default App;
