import React from 'react'
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';



const Inputs = () =>{
  const [value, setValue] = useState('')
  const [array, setArray] = useState([])
  const InputVal = (event)=>{
       setValue(event.target.value)


  }

  const added = () =>{
    setArray((arrayData)=>{
     return [...arrayData, value]
    })
   setValue('')

}
    
    return(
        <>
          <h1 className="heading">To Do List</h1>
    <Input style = {{color: 'white'}} className = "title-input" placeholder = "Title" onChange = {InputVal} value = {value.title} name = "title"/>
    <Input type = "textarea" style = {{color: 'white'}} className = "note-input" placeholder = "Note" onChange = {InputVal} value = {value.note} name = "note"/>
    <Button onClick = {added} className = "btn" style = {{backgroundColor: '#80808023'}} color="primary"><AddIcon/></Button>
        </>
    )
}


export default Inputs;
