const App = ()=>{
    // Get the INput USer Value
     const [currentVal, inputVal] = useState('')
    const InputVal = (e) =>{
        let value = e.target.value
        inputVal(value)
    }

    // To get value on click
    const [Name, updateName] = useState();
    const onSumbit = ()=>{
        updateName(currentVal)
    }


    return(
        <>
        <h1>{Name}</h1>
        <input type = 'text' onChange = {InputVal} ></input>
        <button onClick = {onSumbit}>Click me</button>
        </>
    )
}


export default App;
