import React, { useState, useEffect, useReducer } from 'react'
import NoteList from './NoteList'
import Inputs from './Inputs'







const App = () =>{
    const [add, setArray] = useReducer([], ()=>{
        const listData = localStorage.getItem("list")
        return listData ? JSON.parse : []
    })
    const addNote = (value,e) =>{
        if(value.title === '' && value.note === ''){
            return;
        }
    
            setArray((prevData)=>{
                return [...prevData, value]
                })

    }

    const deletebtn = (id) =>{
        setArray((prevData)=>{
          return prevData.filter((val,index)=>{
              return id !== index
          })
        })
    }

    useEffect(()=>{
        localStorage.setItem("list", JSON.stringify (add));
      }, [add])
    
    return(
        <>
        <div className="wrapper">
            <div className="container">
                <Inputs
                createNote = {addNote}
                />
                
                {add.map((val, index) => {
                    return(
                        <NoteList
                    title = {val.title}
                    note = {val.note}
                    key = {index}
                    id = {index}
                    delete = {deletebtn}
                    />
                    ) 
                })}
                
            </div>
        </div>
        </>
    )
}


export default App;
