import React, {useState} from 'react';
import {Link, Route, BrowserRouter as Router, Switch} from 'react-router-dom'
import Table from 'react-bootstrap/Table'
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button'
import axios from 'axios'





const Data =  ({data})=>{
   const [empty, setEmpty] = useState(false)
   const [modalShow, setModalShow] = useState(false);
    const [value, setValue] = useState({
        email: '',
        username: '',
        name: '',
        phone : '',
        website : ''
      })


      const inputValue = (event) =>{
        const {name, value} = event.target
        setValue((prevData) =>{
          return {...prevData,
            [name] : value}
        })
    }
   const deleteUser = async id =>{
      await axios.delete(`http://localhost:3003/users/${id}`)
       window.location.reload()
   }


   const editUser = (id) =>{
      data.filter(users=> users.id === id).map((data,index) =>{
         return setValue(data)
        
 
     
     })
     setModalShow(true)
   }

   const addEdit = async () =>{
      const newId = value.id
      await axios.put(`http://localhost:3003/users/${newId}`)
      window.location.reload()

   }
   
   const dat = data && data.map((data)=>{
      return  <tbody key = {data.id} >
        <tr>
          <td>{data.name}</td>
          <td>{data.username}</td>
          <td>{data.email}</td>
          <td className = "btn"><Link to = {`/home/view/${data.name}`}><Button variant="primary">View</Button></Link>
          <Button onClick = {()=> editUser(data.id)} variant="secondary">Edit</Button>
          <Button onClick = {()=> deleteUser(data.id)} variant="danger">Delete</Button>
          </td>
           </tr>
          </tbody>

     })
    
    return(
       <>
          
         <Table striped bordered hover variant="dark"  className = "table">
         <thead >
         <tr>
            <th>Name</th>
            <th>User Name</th>
            <th>Email</th>
            <th >Action </th>
         </tr>
         </thead>
         {dat}
         </Table>


        {modalShow ?  <div className="bg-modal" >
	    <div className="modal-contents">
		<div className="close" onClick={() => setModalShow(false)}>+</div>
            <h1>Fill In The Fields</h1>
            <input onChange = {inputValue} value = {value.name} name = "name" type = "text" placeholder = "Name"/>
			   <input onChange = {inputValue} value = {value.username} name = "username"  type = "text" placeholder = "User name"/>
            <input onChange = {inputValue} value = {value.email} name = "email"  type = "email" placeholder = "Email"/>
            <input onChange = {inputValue} value = {value.phone} name = "phone"  type = "phone" placeholder = "Phone Number"/>
            <input onChange = {inputValue} value = {value.website} name = "website"  type = "text" placeholder = "website"/>
             <Button onClick = {addEdit}  variant="danger" >Submit</Button> 
             {empty ? <p style = {{color : 'white'}}>Please Fill in your field</p> : null }
	     </div>
        </div> : null}



       </>
    )
}


export default Data
