// Input Form


const App = () =>{
     const [submitted,setSubmitted] = useState(false);
     const [valid, setValid] = useState(false)
     const [value, setValue] = useState({
         fname: '',
         password: ''
     })

     const handleInput = (e)=>{
        setValue({...value, email:e.target.value})
     }
     const onSubmit = (e)=>{
         e.preventDefault()
         if(value.fname && value.password){
             setValid(true)
         }
         setSubmitted(true)

     }


    return(
        <>
        <form onSubmit ={onSubmit}>
        <div>
        <input type="text" onChange = {handleInput} name = "fname" value={value.fname}/>
        {submitted && valid ? <h1>Success</h1>: null} 
        <input type="text" onChange = {handleInput} name = "password" value={value.password}/>
        <button type = 'submit'>Submit</button>
        </div>
        </form>
        
        </>
    )
}


export default App;
