import React , {useRef, useState} from 'react'
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';




const NoteList = (props) =>{
    const [edit, setEdit] = useState(false)
    let inputref = useRef()
    const deletebtn = () =>{
        inputref.current.className = "active"
        setTimeout(() => {
            props.delete(props.id)
        }, 300);
        
    }

    const editbtn = (index, saveref) =>{
        setEdit(true)
        props.save(props.id)
    }

    const canceledit = () =>{
        setEdit(false)
    }

    if(edit){
        return <li  className = "edit-list">
            <Input className = "edit-input"  style = {{color: 'white'}}/>
            <Button color="primary" >Save</Button>
            <Button onClick = {canceledit} color="primary" >Cancel</Button>
        </li>
    }


    return(
        <>
        <ul  className = "list">
        {props.title && props.note ? <li ref={inputref} >
        <IconButton onClick = {deletebtn} className = "delete-btn" color="primary">
        <DeleteIcon/></IconButton>
        <IconButton onClick = {editbtn} className = "edit-btn" color="primary">
        <EditIcon/></IconButton>
        <span>{props.title}</span><li>{props.note}</li></li> : null }  
        </ul>     
         
        </>
    )
}


export default NoteList;
