import React, { useState, useEffect } from   'react'
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';



const Inputs = (props) =>{
// create a state and create string values of array
  const [value, setValue] = useState({
    title: '',
    note: ''
  })
// get the input values and then keep the prevdata plus store the input values with title of their name property
  const InputVal = (event)=>{
    const {name, value} = event.target
    setValue((prevData) =>{
      return {...prevData,
        [name] : value}
    })
 
  }

// passing value through a function coming through App.jsx prop
  const added = () =>{
  props.createNote(value)
  setValue('')
  

}
    
    return(
        <>
          <h1 className="heading">To Do List</h1>
    <Input autoComplete = "off" style = {{color: 'white'}} className = "title-input" placeholder = "Title" onChange = {InputVal} value = {value.title} name = "title"/>
    <Input autoComplete = "off" style = {{color: 'white'}} className = "note-input" placeholder = "Note" onChange = {InputVal} value = {value.note} name = "note"/>
    <Button onClick = {added} className = "btn" style = {{backgroundColor: '#80808023'}} color="primary"><AddIcon/></Button>
        </>
    )
}


export default Inputs;
