import React, { useState, useEffect } from 'react'
import NoteList from './NoteList'
import Inputs from './Inputs'







const App = () =>{

    // const listData = localStorage.getItem("list")
    // return listData ? JSON.parse : []
    const [add, setArray] = useState([])
    
    const addNote = (value,e) =>{
        if(value.title === '' && value.note === ''){
            return;
        }
    
            setArray((prevData)=>{
                return [...prevData, value]
                
                })

    }

    const deletebtn = (id) =>{
        setArray((prevData)=>{
          return prevData.filter((val,index)=>{
              return id !== index
          })
        })
    }


    const savehandle = (id, input ) =>{
        const data = add.map((e,index) => {
            if(index === id){
                return{
                    ...e,
                    note:input
                }
            }
            return e
        });
        setArray(data)
    }

    useEffect (()=>{
        const listData = localStorage.getItem("list")
        if(listData){
            setArray(JSON.parse(listData))
        }
    }, [])

    useEffect(()=>{
        localStorage.setItem("list", JSON.stringify (add));
      }, [add])
    
    return(
        <>
        <div className="wrapper">
            <div className="container">
                <Inputs
                createNote = {addNote}
                />
                
                {add.map((val, index) => {
                    return(
                        <NoteList
                    title = {val.title}
                    note = {val.note}
                    key = {index}
                    id = {index}
                    save = {savehandle}
                    delete = {deletebtn}
                    />
                    ) 
                })}
                
            </div>
        </div>
        </>
    )
}


export default App;
