import React, { useState, useEffect } from 'react'
import NoteList from './NoteList'
import Inputs from './Inputs'



const App = () =>{
// get the input value and store it in an array
    const [add, setArray] = useState([])
    
    const addNote = (value,e) =>{
        if(value.title === '' && value.note === ''){
            alert("Please Fill In Your Input Fields")
            return;
        }
    
    setArray((prevData)=>{
     return [...prevData, value]
     
                
    })

    }
// Delete list and return only the list which is not equal to the id clicked
    const deletebtn = (id) =>{
        setArray((prevData)=>{
          return prevData.filter((val,index)=>{
              return id !== index
          })
        })
    }

// get the edit input value and add it to the prevdata note value
    const savehandle = (id, input ) =>{
        const data = add.map((newVal,index) => {
            if(index === id){
                return{
                    ...newVal,
                    note:input
                }
            }
            return newVal
        });
        setArray(data)
    }

// if we have the data then update the state value and show from local storage on refresh
    useEffect (()=>{
        const listData = localStorage.getItem("list")
        if(listData){
            setArray(JSON.parse(listData))
        }
    }, [])

    // Setting the setArray to local storage depending on the add submit event
    useEffect(()=>{
        localStorage.setItem("list", JSON.stringify (add));
      }, [add])


    
    return(
        <>
        <div className="wrapper">
            <div className="container">
                <Inputs
                createNote = {addNote}
                />
                {/* Looping through the setArray array and passing values to the list with props */}
                {add.map((val, index) => {
                    return(
                        <NoteList
                    title = {val.title}
                    note = {val.note}
                    key = {index}
                    id = {index}
                    save = {savehandle}
                    delete = {deletebtn}
                    />
                    ) 
                })}
                
            </div>
        </div>
        </>
    )
}


export default App;
