    const p = props.type

    const auth =   JSON.parse( localStorage.getItem( "auth") )
    // if(p === 'guest' && !auth) return <Redirect exact to= "/home"/>
    // if(p === 'private' && auth) return <Redirect exact to= "/"/>

    if(auth ){return <Redirect exact to= "/home"/>}
    else {
        return <Redirect exact to= "/login"/>
    }
    
    // return <Route {...props}/>


}




export default Protected;


