import React, { useState } from 'react'
import NoteList from './NoteList'
import Inputs from './Inputs'







const App = () =>{
    const [submitted,setSubmitted] = useState(false);
    const [add, setArray] = useState([])
    const addNote = (value) =>{
    
            setArray((prevData)=>{
                if (submitted && !value.title && !value.note){
                   return [...prevData, value]
                    setSubmitted(true)
                }
                
              else  {  null}  
                
            })
        
     
    }
    
    return(
        <>
        <div className="wrapper">
            <div className="container">
                <Inputs
                createNote = {addNote}
                />
                
                {add.map((val, index) => {
                    return(
                        <NoteList
                    title = {val.title}
                    note = {val.note}
                    key = {index}
                    id = {index}
                    />
                    ) 
                })}
                
            </div>
        </div>
        </>
    )
}


export default App;
