import React, { useState } from 'react';
import { withRouter } from "react-router";





const Auth = (props)=>{
  
    const [Register, setRegister] = useState()
    const [empty, setEmpty] = useState(false)
    const [error, setError] = useState(false)
    const [value, setValue] = useState({
        email: '',
        password: ''
      })


    const inputValue = (event) =>{
        const {name, value} = event.target
        setValue((prevData) =>{
          return {...prevData,
            [name] : value}
        })
        
    }


    const Onsubmit = (e) =>{
      
      if(value.email === '' && value.password === ''){
        setEmpty(true)
        setTimeout(() => {
          setEmpty(false)
        }, 2000);
        return ;
      }
     
      
      try{ fetch('http://localhost:8000/auth/login', {
        method : 'POST',
        headers : {
          "Accept" : 'application/json',
          "Content-Type" : 'application/json'
        },
        body : 
         JSON.stringify(value)
       }).then( response => {
        if (!response.ok) {
          setError(true)
          e.preventDefault()
        setTimeout(() => {
          setError(false)
        }, 2000); 
          return
        }
        else {
            return response.json() 
        }
    }).then((result) => {
      if(result && value !== 'undefined' && value !== null){
        localStorage.setItem("auth",JSON.stringify(result.access_token))
        props.history.push('/home')
          
      }
      //  {result && value !== 'undefined' && value !== null ? localStorage.setItem("auth",JSON.stringify(result.access_token)) && window.location.reload() : console.log('null')   }
       
       } )
       
      }
      catch (error) {
        console.log(error)
      }
      
    }

    const onRegister = () =>{
     

      if(value.email === '' && value.password === ''){
        setEmpty(true)
        window.location.reload(false)
        setTimeout(() => {
          setEmpty(false)
        }, 2000);
        return ;
      }
      try{ fetch('http://localhost:8000/auth/register', {
        method : 'POST',
        headers : {
          "Accept" : 'application/json',
          "Content-Type" : 'application/json'
        },
        body : 
         JSON.stringify(value)
       })
       .then(response => response.json()).then((result) => {
        
       {  value !== 'undefined' && value !== null ? localStorage.setItem("auth",JSON.stringify(result.access_token)) : console.log('null') }
       window.location.reload()
       props.history.push('/home')
       
         
       })
       
      }
      catch (error) {
        console.log(error)
      }
      
    }


    return(
        <>
        {!Register ?
         <div>
         <input type='text' onChange = {inputValue} value = {value.email} name = "email"></input> <br/> 
         {error ? <p style = {{color : 'white'}}>Incorrect email</p> : null } 
         <input type='password' onChange = {inputValue} value = {value.password} name = 'password'></input><br/>
         {error ? <p style = {{color : 'white'}}>Incorrect password</p> : null } 
        <button onClick = {Onsubmit}>Login</button> 
        <button onClick = {() => setRegister(true) }>Register</button> 
        {empty ? <p style = {{color : 'white'}}>Please Fill in your field</p> : null }
        </div>
        :
        <div>
        <input type='text' onChange = {inputValue} value = {value.email} name = "email"></input> <br/>
        {error ? <p style = {{color : 'white'}}>Incorrect password</p> : null } 
        <input type='password' onChange = {inputValue} value = {value.password} name = 'password'></input><br/>
        {error ? <p style = {{color : 'white'}}>Incorrect password</p> : null } 
        
        <button onClick = {onRegister}>Register</button> 
        <button onClick = {() => setRegister(false)}>Login</button> 
        {empty ? <p style = {{color : 'white'}}>Please Fill in your field</p> : null }
        </div>
         }
       

       
        </>
    )
}


export default Auth
