import React, { useState }  from 'react';




const Listnote = (props) =>{
    const [hide, setHide] = useState(false)
    const [value, setValue] = useState({
        title : '',
        para : ''
    }
    )

    const inputVal = (e) =>{
        const val = e.target.value;
        const name = e.target.name;

        setValue((olddata)=>{
            return {...olddata, [name] : val}
        })
      
    }


    const added = ()=>{
        props.button(value)
        setValue({
            title : '',
            para : ''
        })
    }

    const hideText = ()=>{
        setHide(true)
    }

    const expandNone = ()=>{
        setHide(false)
    }


    return(
        <div>
            <input onDoubleClick = {expandNone} autoComplete = "off" onClick = {hideText} placeholder= "title" onChange = {inputVal} value = {value.title} name = "title"/><br/>
         {hide ?  <textarea  rows="" cols="" onChange = {inputVal} value = {value.para} name="para" /> : null}  
            <button onClick = {added} >Add</button>
        </div>
    )
}


export default Listnote ;
