import React , {useRef} from 'react'
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';




const NoteList = (props) =>{
    let inputref = useRef()
    const deletebtn = () =>{
        inputref.current.className = "active"
        console.log(inputref)
        setTimeout(() => {
            props.delete(props.id)
        }, 200);
        
    }
    return(
        <>
         <ul ref={inputref} className = "list">
       {props.title && props.note ? <li ><IconButton onClick = {deletebtn} className = "delete-btn" color="primary">
        <DeleteIcon/></IconButton><span>{props.title}</span><li>{props.note}</li></li> : null }  
        </ul>     
         
        </>
    )
}


export default NoteList;
