import React, { useState } from 'react';




const Auth = ()=>{
    const [auth, setAuth] = useState()
    const [value, setValue] = useState({
        email: '',
        password: ''
      })


    const inputValue = (event) =>{
        const {name, value} = event.target
        setValue((prevData) =>{
          return {...prevData,
            [name] : value}
        })
        
    }


    const Onsubmit = () =>{
        fetch('http://localhost:8000/auth/login', {
           method : 'POST',
           headers : {
             "Accept" : 'application/json',
             "Content-Type" : 'application/json'
           },
           body : 
            JSON.stringify(value)
        
          })
          .then(response => response.json()).then((result) => {
            const key = localStorage.setItem(JSON.stringify(result.access_token))
            setAuth(key)
            console.log(auth)
          })
          
    }
    return(
        <>
        <div>
            <input type='text' onChange = {inputValue} value = {value.email} name = "email"></input> <br/>
            <input type='password' onChange = {inputValue} value = {value.password} name = 'password'></input><br/>
           <button onClick = {Onsubmit}>Login</button> 
        </div>
        </>
    )
}


export default Auth;
